using System.Text.RegularExpressions;
using Microsoft.VisualBasic;

namespace ConsoleApp2.day6;

internal class Day6_1 : Day
{
    protected override string testInput { get; } = @"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg";
    protected override string input { get; } = @"";

    protected override void Run(string input)
    {
        var queue = new Queue<char>();
        var i = 0;
        foreach (var c in input)
        {
            i++;
            queue.Enqueue(c);

            if (queue.Count < 4)
                continue;

            if (queue.Distinct().Count() == 4)
                break;
            else
                queue.Dequeue();
        }

        Console.WriteLine(i);
    }
}

